/* end of MediaWiki:FR2015/Resources/FullScreenLocalize-SymbolVariant.js */

// Logic that determines mw.centralNotice.bannerData.hideResult
/* MediaWiki:FR2014/Resources/ShowHideCheckFullscreen.js
 * Decide whether to show banner, based on if they saw a "fullscreen"
 * one already.
 *
 * If we do show one, then set a cookie to remember it.
 */

(function(mw) {

    // See https://stackoverflow.com/questions/6125330/javascript-navigator-cookieenabled-browser-compatibility#15582757
    var cookiesEnabled = ('cookie' in document && (document.cookie.length > 0 || (document.cookie = 'test').indexOf.call(document.cookie, 'test') > -1));
    var forced = mw.util.getParamValue('force');

    if ( cookiesEnabled ) {

        if ( $.cookie('centralnotice-frbanner-seen-fullscreen') && !forced ) {
            // Already seen a "fullscreen" banner, so hide this one (unless forced)
            mw.centralNotice.bannerData.hideResult = true;
            mw.centralNotice.bannerData.hideReason = 'seen-fullscreen';
        } else {
            mw.centralNotice.bannerData.hideResult = false;
            // And set a cookie to remember they saw a fullscreen
            $.cookie('centralnotice-frbanner-seen-fullscreen', Date.now(), {expires: 250});
        }

    } else {
        mw.centralNotice.bannerData.hideResult = true;
        mw.centralNotice.bannerData.hideReason = 'cookies-disabled';
    }

})(mediaWiki);

// Move to random choice of bucket C or D
/* MediaWiki:FR2014/Resources/ChangeBucket.js
 * Move user to random choice of bucket C or D for this campaign.
 * Although if it was a test (triggered using banner= in URL)
 * then don't bother as there was no campaign.
 */

(function(mw) {
    if ( !mw.centralNotice.data.getVars.banner ) {
        var newBucket = Math.floor(Math.random()  *  2)  +  2;
        
        if ( mw.cnBannerControllerLib ) {
            // Legacy method
            mw.cnBannerControllerLib.bucketsByCampaign[''].val = newBucket;
            mw.cnBannerControllerLib.storeBuckets();
        } else {
            // Shiny new method
            mw.centralNotice.setBucket( newBucket );
        }
    }
})(mediaWiki);

/* MediaWiki:FR2014/Resources/SeenCountB.js
 * Track the total number of banners the user has seen
 * DEPRECATED: better to use impression diet campaign mixin for this now
 */
 
(function(mw) {
 
    var cookieCount = parseInt($.cookie('centralnotice_bannercount_fr15')) || 0;
 
    if(!mw.centralNotice.bannerData.hideResult) {
        cookieCount += 1;
    }
 
    $.cookie('centralnotice_bannercount_fr15', cookieCount, { expires: 365, path: '/' });
    mw.centralNotice.bannerData.cookieCount = cookieCount;
 
})(mediaWiki);
/** Provides alterImpressionData hook for CentralNotice
 *  This info will be sent back with Special:RecordImpression
 */

mediaWiki.centralNotice.bannerData.alterImpressionData = function( impressionData ) {
    // Returning true from this function indicates the banner was shown
    if (mediaWiki.centralNotice.bannerData.hideReason) {
        impressionData.reason = mediaWiki.centralNotice.bannerData.hideReason;
    }
    if (mediaWiki.centralNotice.bannerData.cookieCount) {
        impressionData.banner_count = mediaWiki.centralNotice.bannerData.cookieCount;
    }
    return !mediaWiki.centralNotice.bannerData.hideResult;
};
